mapper.select:
SELECT * FROM mayreply
WHERE bno = #{bno}
ORDER BY rno ASC

mapper.update:
UPDATE Mayblog SET title = #{title}, content = #{content}, writer = #{writer},
filename = #{filename}, filesize = #{filesize}
WHERE bno = #{bno}

UPDATE Mayblog 
	SET viewcnt = viewcnt + 1
	WHERE bno = #{bno}

UPDATE maymember SET
name=#{name},
phone=#{phone},
email=#{email} 
WHERE id=#{id}

UPDATE maymember SET
pw = #{pw}
WHERE id = #{id}
